	CREATE EXTERNAL DATA SOURCE ds_datalake
WITH (
    LOCATION = 'abfs://raw@nequidl.dfs.core.windows.net/',
    TYPE = HADOOP
);

CREATE EXTERNAL FILE FORMAT csv_format
WITH (
    FORMAT_TYPE = DELIMITEDTEXT,
    FORMAT_OPTIONS (
        FIELD_TERMINATOR = ',',
        STRING_DELIMITER = '"',
        FIRST_ROW = 2  -- Asume que la primera fila tiene encabezados
    )
);

CREATE EXTERNAL FILE FORMAT parquet_format
WITH (
    FORMAT_TYPE = PARQUET
);
